// ==========================================================================

@function em($pixels, $context: $font-size-default) {
  @if (unitless($pixels)) {
    $pixels: $pixels * 1px;
  }

  @if (unitless($context)) {
    $context: $context * 1px;
  }
  @return #{$pixels / $context}em;
}

@function rem($pixels) {
  @if (unitless($pixels)) {
    $pixels: $pixels * 1px;
  }
  @return #{$pixels / $font-size-default}rem;
}


@function font-size($size, $rem: true) {
  $result: $size;
  @if map-has-key($font-sizes, $size) {
    $result: map-get($font-sizes, $size);
  }

  @if $rem == true {
    @return rem($result);
  }

  @return $result;
