.u-text--left {
  text-align: left;
}

.u-text--right {
  text-align: right;
}

.u-text--center {
  text-align: center;
}

//@see bootstrap
.u-text--hide {
  border: 0;
  background-color: transparent;
  color: transparent;
  font: 0 / 0 a;
  font-size: 0.1px;
  text-shadow: none;
}

.u-text--ellipsis {
  @include text-ellipsis;
}

.u-visuallyhidden {
  @include visually-hidden;

  .focusable {

    &:active,
    &:focus {
      position: static;
      overflow: visible;
      clip: auto;
      width: auto;
      height: auto;
      margin: 0;
    }
  }
}

.u-invisible {
  visibility: hidden;
}

@each $mq-key, $mq-value in $mq-breakpoints {
  @include mq($mq-key) {

    .u-hidden--from-#{$mq-key} {
      display: none !important;
      visibility: hidden;
    }
  }

  @include mq($until: $mq-key) {

    .u-hidden--until-#{$mq-key} {
      display: none !important;
      visibility: hidden;
    }
  }
}

.u-align {
  display: flex;
  flex-flow: column;
  justify-content: center;

  &--top-left {
    top: 0;
    left: 0;
  }
  &--top-right {
    top: 0;
    right: 0;
    text-align: right;
  }

  &--center-left {
    top: 0;
    bottom: 0;
    left: 0;
  }

  &--center-right {
    top: 0;
    bottom: 0;
    right: 0;
    text-align: right;
  }

  &--bottom-left {
    bottom: 0;
    right: 0;
  }

  &--bottom-right {
    bottom: 0;
    right: 0;
    text-align: right;
  }
}

